var searchStart=function(e){e&&(window.location.href=encodeURI("/?search="+e))};$(".search").click(function(){var e=$(this).siblings(".search-input").val();searchStart(e)}),$(window).keypress(function(e){if(13==e.keyCode)if("none"==$(".header-mobile").css("display")){var a=$(".header-right").find(".search-input").val();searchStart(a)}else{var a=$(".header-mobile").find(".search-input").val();searchStart(a)}});